<style>
  header {
    grid-area: 1 / 1 / 2 / 5;
    display: flex;
    justify-content: space-between;
    align-items: center;
    height: 128px;
    background-color: var(--background);
  }

   header::after {
     content: '';
     height: 1px;
     width: calc( 100% + 2rem );
     position: absolute;
     background-color: var(--primary-dark);
     left: -1rem;
     top: 128px;
   }

  .logo {
    width: 66px;
    height: 78px;
    padding-left: 3rem;
 }

 .nav {
   padding-right: 3rem;
 }

 ul {
   list-style: none;
   padding: 0;
 }

 ul li a {
    font-family: var(--body-text);
    font-size: 0.75rem;
    color: var(--primary-light);
    letter-spacing: 0.1rem;
    line-height: 1.4rem;
 }

 ul li a:hover {
    color: var(--accent-primary);
 }

 .social {
   display: none;
 }

 .social ul li a svg:hover > * {
    fill: var(--accent-primary);
 }

 @media screen and (min-width: 1048px) {
    header {
      grid-area: 1 / 2 / 2 / 8;
    }

    ul li {
      display: inline-block;
      padding: 4px;
    }

    .social {
      display: block;
    }

    .social ul {
      position: relative;
      top: 6px;
    }
  }
</style>

